mapper.select:
select original_file, img_url
from review_file_info
where review_id = #{reviewId}

mapper.delete:
DELETE
FROM review
WHERE review_id = #{reviewId}
  and user_id = #{userId}

mapper.update:
UPDATE review
SET title   = #{title},
    content = #{content},
    rating  = #{rating}
WHERE review_id = #{reviewId}
  and user_id = #{userId}
